// Unit test example with Jest
function calculateTotal(items, taxRate = 0.1) {
  const subtotal = items.reduce((sum, item) => sum + item.price * item.quantity, 0);
  return subtotal * (1 + taxRate);
}

describe('calculateTotal', () => {
  test('calculates total correctly with default tax', () => {
    const items = [
      { price: 10, quantity: 2 },  // $20
      { price: 15, quantity: 1 }   // $15
    ];  // Subtotal: $35, Tax: $3.50, Total: $38.50

    expect(calculateTotal(items)).toBeCloseTo(38.5);
  });

  test('calculates total with custom tax rate', () => {
    const items = [{ price: 100, quantity: 1 }];
    expect(calculateTotal(items, 0.05)).toBeCloseTo(105);
  });

  test('handles empty items array', () => {
    expect(calculateTotal([])).toBeCloseTo(0);
  });
});

