// Arrange-Act-Assert pattern with proper test structure
class ShoppingCart {
  constructor() {
    this.items = [];
  }

  addItem(item) {
    this.items.push(item);
  }

  calculateTotal() {
    return this.items.reduce((total, item) => total + (item.price * item.quantity), 0);
  }

  getItemCount() {
    return this.items.reduce((count, item) => count + item.quantity, 0);
  }

  clear() {
    this.items = [];
  }
}

describe('ShoppingCart', () => {
  let cart;

  beforeEach(() => {
    // Arrange - Set up fresh state for each test
    cart = new ShoppingCart();
  });

  afterEach(() => {
    // Clean up after each test if needed
    cart.clear();
  });

  test('calculates total correctly with multiple items', () => {
    // Arrange
    cart.addItem({ name: 'Apple', price: 1.5, quantity: 3 });
    cart.addItem({ name: 'Banana', price: 0.75, quantity: 2 });

    // Act
    const total = cart.calculateTotal();

    // Assert
    expect(total).toBeCloseTo(6.0); // (1.5 * 3) + (0.75 * 2) = 4.5 + 1.5 = 6.0
  });

  test('returns correct item count', () => {
    // Arrange
    cart.addItem({ name: 'Book', price: 20, quantity: 1 });
    cart.addItem({ name: 'Pen', price: 2, quantity: 5 });

    // Act
    const count = cart.getItemCount();

    // Assert
    expect(count).toBe(6); // 1 + 5 = 6
  });

  test('starts with zero items', () => {
    // Act & Assert
    expect(cart.getItemCount()).toBe(0);
    expect(cart.calculateTotal()).toBe(0);
  });
});

// Using mocks to isolate units of code
const database = require('../database');
const emailService = require('../emailService');
const userService = require('../userService');

// Mock external dependencies
jest.mock('../database');
jest.mock('../emailService');

describe('User service', () => {
  beforeEach(() => {
    // Clear all mocks before each test
    jest.clearAllMocks();
  });

  test('sends welcome email after user creation', async () => {
    // Arrange
    const userData = { email: 'test@example.com', name: 'Test User' };
    const mockUser = { id: 1, ...userData };

    // Mock database save operation
    database.saveUser.mockResolvedValue(mockUser);

    // Act
    const user = await userService.createUser(userData);

    // Assert
    expect(database.saveUser).toHaveBeenCalledWith(userData);
    expect(emailService.sendWelcomeEmail).toHaveBeenCalledWith(mockUser);
    expect(user).toEqual(mockUser);
  });

  test('handles database errors gracefully', async () => {
    // Arrange
    const userData = { email: 'test@example.com', name: 'Test User' };
    const dbError = new Error('Database connection failed');

    database.saveUser.mockRejectedValue(dbError);

    // Act & Assert
    await expect(userService.createUser(userData)).rejects.toThrow('Database connection failed');
    expect(emailService.sendWelcomeEmail).not.toHaveBeenCalled();
  });
});

// Testing asynchronous code with async/await
const apiClient = require('../apiClient');

jest.mock('../apiClient');

describe('API Service', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  test('fetches user data successfully', async () => {
    // Arrange
    const userId = 123;
    const mockUser = { id: 123, name: 'John Doe', email: 'john@example.com' };
    apiClient.getUser.mockResolvedValue(mockUser);

    // Act
    const user = await apiClient.getUser(userId);

    // Assert
    expect(apiClient.getUser).toHaveBeenCalledWith(userId);
    expect(user).toEqual(mockUser);
  });

  test('handles API errors gracefully', async () => {
    // Arrange
    const userId = 123;
    const apiError = new Error('User not found');
    apiClient.getUser.mockRejectedValue(apiError);

    // Act & Assert
    await expect(apiClient.getUser(userId)).rejects.toThrow('User not found');
  });

  test('retries failed requests', async () => {
    // Arrange
    const userId = 123;
    const networkError = new Error('Network error');
    const mockUser = { id: 123, name: 'John Doe' };

    // First call fails, second succeeds
    apiClient.getUser
      .mockRejectedValueOnce(networkError)
      .mockResolvedValueOnce(mockUser);

    // Act
    const user = await apiClient.getUser(userId);

    // Assert
    expect(apiClient.getUser).toHaveBeenCalledTimes(2);
    expect(user).toEqual(mockUser);
  });
});

// Mocking modules with complex behavior
const fs = require('fs');
const fileProcessor = require('../fileProcessor');

jest.mock('fs');

describe('File Processor', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  test('processes file successfully', async () => {
    // Arrange
    const filePath = '/path/to/file.txt';
    const fileContent = 'Hello, World!';
    const processedContent = 'HELLO, WORLD!';

    // Mock fs.readFile to return specific content
    fs.readFile.mockImplementation((path, encoding, callback) => {
      callback(null, fileContent);
    });

    // Act
    const result = await fileProcessor.processFile(filePath);

    // Assert
    expect(fs.readFile).toHaveBeenCalledWith(
      filePath, 
      'utf8', 
      expect.any(Function)
    );
    expect(result).toBe(processedContent);
  });

  test('handles file read errors', async () => {
    // Arrange
    const filePath = '/path/to/nonexistent.txt';
    const fileError = new Error('ENOENT: no such file or directory');

    fs.readFile.mockImplementation((path, encoding, callback) => {
      callback(fileError, null);
    });

    // Act & Assert
    await expect(fileProcessor.processFile(filePath)).rejects.toThrow('File processing failed');
  });
});

// Testing time-dependent code
jest.useFakeTimers();

function delayedGreeting(name, delay) {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve(`Hello, ${name}!`);
    }, delay);
  });
}

test('resolves after specified delay', async () => {
  // Arrange
  const name = 'John';
  const delay = 5000;

  // Act
  const greetingPromise = delayedGreeting(name, delay);

  // Fast-forward until all timers are executed
  jest.advanceTimersByTime(delay);

  // Assert
  await expect(greetingPromise).resolves.toBe('Hello, John!');
});

// Testing REST API endpoints with different scenarios
const request = require('supertest');
const app = require('../app');
const db = require('../database');

// Setup and teardown for integration tests
describe('User API Integration Tests', () => {
  beforeAll(async () => {
    // Setup database connection
    await db.connect();
  });

  afterAll(async () => {
    // Close database connection
    await db.close();
  });

  beforeEach(async () => {
    // Clear test data before each test
    await db.clearUsers();
  });

  describe('POST /api/users', () => {
    test('creates a new user with valid data', async () => {
      const userData = {
        name: 'John Doe',
        email: 'john@example.com',
        password: 'password123'
      };

      const response = await request(app)
        .post('/api/users')
        .send(userData)
        .expect(201)
        .expect('Content-Type', /json/);

      expect(response.body).toMatchObject({
        id: expect.any(Number),
        name: userData.name,
        email: userData.email
      });

      // Password should not be returned
      expect(response.body).not.toHaveProperty('password');

      // Verify user was actually saved to database
      const savedUser = await db.findUserById(response.body.id);
      expect(savedUser).toMatchObject({
        name: userData.name,
        email: userData.email
      });
    });

    test('returns 400 for invalid email', async () => {
      const invalidData = {
        name: 'John Doe',
        email: 'invalid-email',
        password: 'password123'
      };

      const response = await request(app)
        .post('/api/users')
        .send(invalidData)
        .expect(400)
        .expect('Content-Type', /json/);

      expect(response.body).toHaveProperty('error');

      // Verify no user was created
      const users = await db.findAllUsers();
      expect(users).toHaveLength(0);
    });

    test('returns 409 for duplicate email', async () => {
      // First, create a user
      await db.createUser({
        name: 'Existing User',
        email: 'duplicate@example.com',
        password: 'password123'
      });

      // Try to create another user with same email
      const userData = {
        name: 'New User',
        email: 'duplicate@example.com',
        password: 'password456'
      };

      const response = await request(app)
        .post('/api/users')
        .send(userData)
        .expect(409)
        .expect('Content-Type', /json/);

      expect(response.body).toHaveProperty('error', 'Email already exists');
    });
  });

  describe('GET /api/users/:id', () => {
    test('returns user by ID', async () => {
      // First, create a user
      const user = await db.createUser({
        name: 'Test User',
        email: 'test@example.com',
        password: 'password123'
      });

      const response = await request(app)
        .get(`/api/users/${user.id}`)
        .expect(200)
        .expect('Content-Type', /json/);

      expect(response.body).toMatchObject({
        id: user.id,
        name: user.name,
        email: user.email
      });
    });

    test('returns 404 for non-existent user', async () => {
      await request(app)
        .get('/api/users/99999')
        .expect(404);
    });
  });
});

