// Performance testing with Jest
const { performance } = require('perf_hooks');

function measurePerformance(fn, iterations = 1000) {
  const times = [];

  for (let i = 0; i < iterations; i++) {
    const start = performance.now();
    fn();
    const end = performance.now();
    times.push(end - start);
  }

  const avg = times.reduce((sum, time) => sum + time, 0) / times.length;
  const min = Math.min(...times);
  const max = Math.max(...times);

  return { avg, min, max };
}

describe('Performance Tests', () => {
  test('array processing completes within acceptable time', () => {
    const largeArray = Array.from({ length: 10000 }, (_, i) => i);

    const stats = measurePerformance(() => {
      largeArray.filter(n => n % 2 === 0).map(n => n * 2);
    }, 100);

    expect(stats.avg).toBeLessThan(5);
    expect(stats.max).toBeLessThan(20);
  });
});

// Test data factory pattern
class UserFactory {
  static create(overrides = {}) {
    return {
      id: Math.floor(Math.random() * 10000),
      name: 'John Doe',
      email: `user${Date.now()}@example.com`,
      age: 25,
      isActive: true,
      createdAt: new Date(),
      ...overrides
    };
  }

  static createMany(count, overrides = {}) {
    return Array.from({ length: count }, (_, index) => 
      this.create({ ...overrides, id: index + 1 })
    );
  }

  static createAdmin(overrides = {}) {
    return this.create({
      role: 'admin',
      permissions: ['read', 'write', 'delete'],
      ...overrides
    });
  }
}

// Builder pattern for complex objects
class OrderBuilder {
  constructor() {
    this.order = {
      id: Math.floor(Math.random() * 10000),
      status: 'pending',
      items: [],
      total: 0
    };
  }

  withItem(name, price, quantity = 1) {
    const item = { name, price, quantity };
    this.order.items.push(item);
    this.order.total += price * quantity;
    return this;
  }

  build() {
    return { ...this.order };
  }
}

