// Visual testing with Puppeteer
const puppeteer = require('puppeteer');
const pixelmatch = require('pixelmatch');
const { PNG } = require('pngjs');
const fs = require('fs');

class VisualTester {
  async captureScreenshot(url, filename) {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.setViewport({ width: 1200, height: 800 });
    await page.goto(url);
    await page.screenshot({ path: filename });
    await browser.close();
  }

  async compareScreenshots(baseline, current, diff) {
    const img1 = PNG.sync.read(fs.readFileSync(baseline));
    const img2 = PNG.sync.read(fs.readFileSync(current));
    const { width, height } = img1;
    const diffImg = new PNG({ width, height });

    const numDiffPixels = pixelmatch(
      img1.data, img2.data, diffImg.data, width, height
    );

    fs.writeFileSync(diff, PNG.sync.write(diffImg));
    return (numDiffPixels / (width * height)) * 100;
  }
}

// Test usage
test('homepage visual regression', async () => {
  const tester = new VisualTester();
  await tester.captureScreenshot('http://localhost:3000', 'current.png');

  if (fs.existsSync('baseline.png')) {
    const diffPercent = await tester.compareScreenshots(
      'baseline.png', 'current.png', 'diff.png'
    );
    expect(diffPercent).toBeLessThan(2);
  }
});

// Security testing examples
const request = require('supertest');
const app = require('../app');

describe('Security Tests', () => {
  test('prevents SQL injection', async () => {
    const maliciousPayload = {
      email: "'; DROP TABLE users; --",
      password: "password"
    };

    const response = await request(app)
      .post('/api/auth/login')
      .send(maliciousPayload)
      .expect(400);

    expect(response.body.error).toContain('Invalid');
  });

  test('sanitizes XSS attempts', async () => {
    const xssPayload = {
      name: '<script>alert("XSS")</script>',
      bio: '<img src=x onerror=alert("XSS")>'
    };

    const response = await request(app)
      .post('/api/users/profile')
      .set('Authorization', 'Bearer valid-token')
      .send(xssPayload)
      .expect(200);

    expect(response.body.name).not.toContain('<script>');
    expect(response.body.name).toBe('&lt;script&gt;alert("XSS")&lt;/script&gt;');
  });

  test('enforces rate limiting', async () => {
    const attempts = [];
    for (let i = 0; i < 6; i++) {
      attempts.push(
        request(app)
          .post('/api/auth/login')
          .send({ email: 'test@example.com', password: 'wrong' })
      );
    }

    const responses = await Promise.all(attempts);
    expect(responses[5].status).toBe(429);
  });
});

