// Example testing all branches
function calculateDiscount(price, customerType, loyaltyYears) {
  let discount = 0;

  if (customerType === 'premium') {
    discount += 0.2;
  } else if (customerType === 'gold') {
    discount += 0.15;
  }

  if (loyaltyYears >= 5) {
    discount += 0.05;
  }

  return price * (1 - Math.min(discount, 0.3));
}

// Comprehensive test coverage
describe('calculateDiscount', () => {
  test('premium customer discount', () => {
    expect(calculateDiscount(100, 'premium', 0)).toBe(80);
  });

  test('gold customer discount', () => {
    expect(calculateDiscount(100, 'gold', 0)).toBe(85);
  });

  test('loyalty bonus', () => {
    expect(calculateDiscount(100, 'premium', 5)).toBe(75);
  });

  test('discount cap', () => {
    expect(calculateDiscount(100, 'premium', 10)).toBe(70);
  });
});

