// Final implementation driven by tests
class UserService {
  constructor() {
    this.users = [];
    this.nextId = 1;
  }

  register(email, password) {
    this.validateEmail(email);
    this.validatePassword(password);

    const user = {
      id: this.nextId++,
      email: email,
      hashedPassword: this.hashPassword(password)
    };

    this.users.push(user);

    // Return user without password
    return {
      id: user.id,
      email: user.email
    };
  }

  validateEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      throw new Error('Invalid email format');
    }
  }

  validatePassword(password) {
    if (password.length < 8) {
      throw new Error('Password must be at least 8 characters');
    }
  }

  hashPassword(password) {
    // Simplified hashing (use bcrypt in real apps)
    return 'hashed_' + password;
  }
}

// Understanding code coverage types

// Example function to test
function processUser(user) {
  // Line coverage: Are these lines executed?
  if (!user) {
    throw new Error('User is required');
  }

  if (!user.email) {
    throw new Error('Email is required');
  }

  // Branch coverage: Are both true/false branches tested?
  if (user.age >= 18) {
    user.status = 'adult';
  } else {
    user.status = 'minor';
  }

  // Function coverage: Is this function called?
  const result = validateAndFormat(user);

  return result;
}

function validateAndFormat(user) {
  user.email = user.email.toLowerCase();
  return user;
}

// Tests demonstrating different coverage types
describe('processUser - Coverage Examples', () => {
  test('processes valid adult user', () => {
    const user = { email: 'TEST@EXAMPLE.COM', age: 25 };
    const result = processUser(user);

    expect(result.status).toBe('adult');
    expect(result.email).toBe('test@example.com');
  });

  // This test alone gives:
  // - Line coverage: ~80% (doesn't hit minor branch)
  // - Branch coverage: ~50% (only adult branch)
  // - Function coverage: 100% (both functions called)

  test('processes valid minor user', () => {
    const user = { email: 'CHILD@EXAMPLE.COM', age: 16 };
    const result = processUser(user);

    expect(result.status).toBe('minor');
  });

  // Now we have 100% branch coverage

  test('throws error for null user', () => {
    expect(() => processUser(null)).toThrow('User is required');
  });

  test('throws error for user without email', () => {
    expect(() => processUser({ age: 25 })).toThrow('Email is required');
  });

  // Now we have 100% line coverage
});

// Best practices for test organization
describe('User Management Service', () => {
  let userService;
  let mockDatabase;

  beforeEach(() => {
    // Fresh setup for each test
    mockDatabase = {
      save: jest.fn(),
      findById: jest.fn(),
      findByEmail: jest.fn()
    };
    userService = new UserService(mockDatabase);
  });

  describe('user creation', () => {
    test('creates user with valid data', async () => {
      // Arrange
      const userData = createValidUserData();
      mockDatabase.save.mockResolvedValue({ id: 1, ...userData });

      // Act
      const user = await userService.createUser(userData);

      // Assert
      expect(user).toMatchObject({
        id: expect.any(Number),
        email: userData.email
      });
    });

    // Group related tests together
    describe('validation errors', () => {
      test('rejects invalid email format', async () => {
        const userData = createValidUserData({ email: 'invalid-email' });

        await expect(userService.createUser(userData))
          .rejects.toThrow('Invalid email format');
      });

      test('rejects duplicate email', async () => {
        const userData = createValidUserData();
        mockDatabase.findByEmail.mockResolvedValue({ id: 1 });

        await expect(userService.createUser(userData))
          .rejects.toThrow('Email already exists');
      });
    });
  });

  describe('user retrieval', () => {
    test('finds user by ID', async () => {
      const userId = 1;
      const mockUser = { id: userId, email: 'test@example.com' };
      mockDatabase.findById.mockResolvedValue(mockUser);

      const user = await userService.findUser(userId);

      expect(user).toEqual(mockUser);
      expect(mockDatabase.findById).toHaveBeenCalledWith(userId);
    });
  });
});

// Helper functions for test data
function createValidUserData(overrides = {}) {
  return {
    email: 'test@example.com',
    name: 'Test User',
    age: 25,
    ...overrides
  };
}

