// Built-in functions
$base-color: #3498db;

.card {
  background-color: lighten($base-color, 20%);
  border-color: darken($base-color, 15%);
  font-size: 16px * 1.2;
  width: 200px + 50px;
}

// Custom function
@function calculate-rem($size) {
  $rem-size: $size / 16px;
  @return #{$rem-size}rem;
}

.text {
  font-size: calculate-rem(18px);
}
