const app = Vue.createApp({
  data() {
    return {
      message: 'Hello Vue!'
    }
  },
  // Lifecycle hooks
  beforeCreate() {
    console.log('Before create - data and events are not set up yet')
  },
  created() {
    console.log('Created - data and events are active, but DOM not mounted')
    // Good place for API calls
  },
  beforeMount() {
    console.log('Before mount - template and render functions about to be compiled')
  },
  mounted() {
    console.log('Component is mounted!')
    // Good place for DOM manipulation
  },
  beforeUpdate() {
    console.log('Before update - called when data changes, before DOM update')
  },
  updated() {
    console.log('Updated - called after DOM update')
  },
  beforeUnmount() {
    console.log('Before unmount - called before component is destroyed')
  },
  unmounted() {
    console.log('Unmounted - called after component is destroyed')
  }
})

app.mount('#app')

// Define a child component
const BlogPost = {
  template: `
    <div class="blog-post">
      <h3>{{ title }}</h3>
      <p>{{ content }}</p>
      <button @click="$emit('enlarge-text', 0.1)">Enlarge text</button>
      <button @click="$emit('delete-post')">Delete</button>
    </div>
  `,
  // Declare props
  props: ['title', 'content'],
  // Emits declaration (optional but recommended)
  emits: ['enlarge-text', 'delete-post']
}

